			<script type="text/javascript">
(function(){
	errorManager = function(){};

	var codeType;
	var partyId = sessionStorage.getItem('party_id');
	var brtkn = sessionStorage.getItem('sv_tkn_accs');
	if(!partyId) errorManager();
	$(document).on('click','#playButton', function(){
		showGame();
		play_game( codeType=='prismaticos', codeType=='ot', partyId );
		window.onGame('user_score', function(score){
			$('#points').text(score);
		});
	});

	onGame('game_start', function(){
		$.ajax({
			url: path_ws+'/api/party/'+partyId+'/start',
			method:'post',
			dataType: 'json',
			headers: {'Authorization': 'Bearer '+ brtkn},
			success:function(data){
				if(data.error){
					Swal.fire({
					  icon: 'error',
					  title: 'Oops...',
					  text: data.error_description
					});
				}
			}
		});
	});
	onGame('next_level', function(level, points){
		$.ajax({
			url: path_ws+'/api/party/'+partyId+'/score',
			method:'post',
			dataType: 'json',
			headers: {'Authorization': 'Bearer '+ brtkn},
			data: { score:points },
			success:function(data){
				if(data.error) errorManager();
			}
		});
		console.log(points)
	});
	onGame('user_start', function(lifes){
		$('#lives > div').slice(0,6-lifes).hide();
	});
	onGame('user_fruit', function(fruitType, fruitValue, key){
		$('.fruit-icon').eq(key).addClass('fruit-icon-' + fruitType);
	});
	onGame('finish', function(points, delay, log){
		hideAll();
		showFinish(points, delay, log);
	});
	onGame('user_die', function(lifes){
		$('#lives .panman-icon').hide().slice(0, lifes).show();
	});
	onGame('control', function(dir){
		var direction=['up','down','left','right'];
		$('#controlContainer').removeClass('up down left right').addClass(direction[dir]);
	})

	$(document).on('click','.btn-insertOtherCode',function(){
		errorManager();
	});
	$(document).on('click','.btn-gototprofile',function(){
		window.location='perfil.php';
	});

	hideAll = function(){ $('#main .game_mark section').hide(); }
	showIntro = function(){
		hideAll();
		$('#game_intro').show()
		.addClass( codeType=='prismaticos'? 'doublelifes': '' )
		.addClass( codeType=='ot'? 'doublepoints': '' );
	}
	showGame = function(){ hideAll(); $('#game_game').show(); }
	showFinish = function(points,time,log){
		$.ajax({
			url: path_ws+'/api/party/'+partyId+'/end',
			method:'post',
			dataType: 'json',
			headers: {'Authorization': 'Bearer '+ brtkn},
			data: { score:points, log:JSON.stringify(log) },
			success:function(data){
				if(data.error) errorManager();
			}
		});
		$(document.body).removeClass('playing');
		points *= codeType=='ot'? 2: 1;
		hideAll();
		$('#game_finish').show();
		$('#game_points').text(points);
		$('#game_time').text(time);
	}
	showError = function(){ hideAll(); $('#game_error').show(); }

	$.ajax({
		url: path_ws+'/api/party/'+partyId,
		method:'get',
		dataType: 'json',
		headers: {'Authorization': 'Bearer '+ brtkn},
		success:function(data){
			if(data.error || data.status!='pending'){ errorManager(); }
			else{
				codeType=data.code.category;
				functionSuccesCode();
				showIntro();
			}
		}
	});

})();


			</script>

