"use strict";

/* animationUtilities stellt den Namespace für Animationen auf der Seite bereit */
var animationUtilities = {
    /* Bewegt das Element über die Seite, Geschwindigkeit einstellbar, eintrag in Teaser-JS: animationUtilities.flyOverPage(teaser, 0.05); */
    flyOverPage: function (identifier, speedModifier) {
        if ($(identifier).parent()[0].tagName !== "BODY") {
            $(identifier).detach();
            $(identifier).appendTo("body");
            $(identifier).css("position", "absolute");
            $(identifier).css("z-index", "999");
            $(identifier).css("cursor", "pointer");
            $(identifier).on("click", function() {
                // Richtungsänderung
                $(identifier).stop();
                animationUtilities.flyOverPage(identifier, speedModifier);
            });
        }

        // Neues Zufallsziel
        var newTarget = animationUtilities.getTargetPosition($(identifier).width(), $(identifier).height());
        var oldPosition = $(identifier).offset();

        // Mindestabstand muss vorliegen
        var calculationTries = 3;
        var currentDistance = animationUtilities.getDistance([oldPosition.top, oldPosition.left], newTarget);
        while (calculationTries > 0 && currentDistance < 200.0) {
            // Neues Ziel anfordern
            newTarget = animationUtilities.getTargetPosition($(identifier).width(), $(identifier).height());
            currentDistance = animationUtilities.getDistance([oldPosition.top, oldPosition.left], newTarget);

            calculationTries--;
        }

        var speed = animationUtilities.calculateSpeed([oldPosition.top, oldPosition.left], newTarget, speedModifier);

        $(identifier).animate({top: newTarget[0], left: newTarget[1]}, speed, "linear", function () {
            animationUtilities.flyOverPage(identifier, speedModifier);
        });
    },

    getTargetPosition: function(widthOfObject, heightOfObject) {
        var maxHeight = $(document).height() - heightOfObject;
        var maxWidth = $(document).width() - widthOfObject;

        var targetHeight = Math.floor(Math.random() * maxHeight);
        var targetWidth = Math.floor(Math.random() * maxWidth);

        return [targetHeight, targetWidth];
    },

    calculateSpeed: function (oldPosition, newPosition, speedModifier) {
        var x = Math.abs(oldPosition[1] - newPosition[1]);
        var y = Math.abs(oldPosition[0] - newPosition[0]);

        var greatest = x > y ? x : y;
        var speed = Math.ceil(greatest / speedModifier);

        return speed;
    },

    getDistance: function (sourcePosition, targetPosition) {
        var x = Math.abs(sourcePosition[1] - targetPosition[1]);
        var y = Math.abs(sourcePosition[0] - targetPosition[0]);
        return Math.sqrt(x * x + y * y);
    },

    genericAnimationGetClassName: function(animationId) {
        var result = "animated";
        switch (parseInt(animationId)) {
            case 1:
                result += " tada slow infinite";
                break;
            case 2:
                result += " jello slow infinite";
                break;
            case 3:
                result += " rubberBand slow infinite";
                break;
            case 4:
                result += " rollIn";
                break;
            case 5:
                result += " lightSpeedIn";
                break;
            case 6:
                result += " bounceInLeft slow";
                break;
            case 7:
                result += " bounceInRight slow";
                break;
            case 8:
                result += " tada slow";
                break;
            case 9:
                result += " jello slow";
                break;
            case 10:
                result += " rubberBand slow";
                break;
            case 11:
                result += " lightSpeedInLeft";
                break;
            default:
                return "";
        }

        return result;
    }
}

$(window).on("load", function() {
    // Einmalige Animationen erst im Scrollbereich
    var animationElements = [].slice.call(document.querySelectorAll(".animateifvisible"));

    if ("IntersectionObserver" in window) {
        console.log("EoA: Animation IntersectionObserver");

        var animationObserver = new IntersectionObserver(function(entries, observer) {
            entries.forEach(function (entry) {
                if (entry.isIntersecting) {
                    console.log(entry);

                    var element = $(entry.target).find(".animateifvisible");
                    element.addClass("animated");
                    element.show();
                    animationObserver.unobserve(entry.target);
                }
            });
        }, {
            rootMargin: '0px 0px -15% 0px',
            threshold: 0.15
        });

        animationElements.forEach(function (element) {
            var elementHeight = element.clientHeight;
            $(element.parentElement).css("min-height", elementHeight);

            $(element).hide();
            animationObserver.observe(element.parentElement);
        });
    } else {
        console.log("EoA: Animation Fallback");

        // Fallback
        var active = false;

        var animateIfVisible = function() {
            if (active === false) {
                active = true;

                setTimeout(function() {
                    var imagesLength = animationElements.length;
                    var imagesToRemove = [];
                    for (var i = 0; i < imagesLength; i++) {
                        if ((animationElements[i].getBoundingClientRect().top <= window.innerHeight
                                && animationElements[i].getBoundingClientRect().bottom >= 0))
                        {
                            var element = $(animationElements[i]);
                            element.addClass("animated");
                            element.show();

                            // image entfernen
                            imagesToRemove.push(animationElements[i]);
                        }
                    }

                    // Images entfernen
                    animationElements = animationElements.filter(function (image) {
                        return $.inArray(image, imagesToRemove) == -1;
                    });

                    if (animationElements.length === 0) {
